// Type declarations for plotly.js bundles
declare module 'plotly.js-dist-min' {
  export * from 'plotly.js';
  import Plotly from 'plotly.js';
  export default Plotly;
}

declare module 'plotly.js-basic-dist-min' {
  export * from 'plotly.js';
  import Plotly from 'plotly.js';
  export default Plotly;
}